x-request-examples-2.template_body:
terraform {
    required_providers {
      huaweicloud = {
        source = "huawei.com/provider/huaweicloud"
        version = "1.41.0"
        }
    }
}
provider "huaweicloud"{
    insecure = true
    cloud = "{cloud_name}"
    region = "{region}"
    endpoints = {
        iam = "{iam_endpoint}",
    }
}

resource "huaweicloud_vpc" "vpc" {
  cidr = "172.16.0.0/16"
  name = "my_vpc"
}
